 SELECT min_station.city, min_station.len FROM STATION LEFT JOIN (SELECT id, CITY, LENGTH(CITY) as len  FROM STATION ORDER BY LENGTH(CITY) asc LIMIT 1) min_station ON STATION.id=min_station.id where STATION.id=min_station.id UNION SELECT max_station.city, max_station.len FROM STATION LEFT JOIN (SELECT id, CITY, LENGTH(CITY) as len  FROM STATION ORDER BY LENGTH(CITY) desc LIMIT 1) max_station ON STATION.id=max_station.id where STATION.id=max_station.id;
